'with fltr as (',
'    select decode(nvl(:P1_PRODUCT,0),0,'''',''IR_PRODUCT_ID'') cols,',
'        decode(nvl(:P1_PRODUCT,0),0,'''',:P1_PRODUCT) vals,',
