'      created >= sysdate - :P52_SINCE and',
'      (NVL(:P52_FIX_BY,0) = 0 or :P52_FIX_BY = b.FIX_BY_VERSION_ID) ',
'group by  to_char(date_closed,''YYYY'')||decode(to_char(date_closed,''WW''), ''53'', ''52'', to_char(date_closed,''WW''))',
