,p_condition=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select null',
'from user_tables',
'where table_name = ''EBA_BT_USER'';'))
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'alter table eba_bt_users',
'add ( external_username varchar2(255),',
'    first_name varchar2(50),',
'    last_name varchar2(50),',
'    email varchar2(255),',
'    work_phone varchar2(10)',
');',
'/',
'',
'create or replace trigger eba_bt_users_biu',
'    before insert or update on eba_bt_users',
'    for each row',
'begin',
'    if inserting then',
'        if :new.id is null then',
'            :new.id := eba_bt.gen_id();',
'        end if;',
'        :new.created_by         := nvl(v(''APP_USER''), USER);',
'        :new.created            := localtimestamp;',
'        :new.row_version        := 1;',
'        if :new.account_locked is null then',
'            :new.account_locked := ''N'';    ',
'        end if;',
'    end if;',
'    if updating then',
'            :new.updated_by         := nvl(v(''APP_USER''), USER);',
'            :new.updated            := localtimestamp;',
'            :new.row_version        := nvl(:old.row_version,1) + 1;                                ',
'    end if;',
'    -- Always store usernames as upper case',
'    :new.username := upper(:new.username);',
'    :new.external_username := upper(:new.external_username);',
'end;',
'/',
'',
'merge into eba_bt_users dest',
'using ( select id,',
'            login_name username,',
'            first_name,',
'            last_name,',
'            email,',
'            work_phone,',
'            case when is_admin = ''Y'' then 3 else 2 end access_level_id',
'        from eba_bt_user',
'    ) src',
'on ( lower(dest.username) = lower(src.username) )',
'when not matched then',
'    insert ( id, username, access_level_id, first_name, last_name, email, work_phone )',
'    values ( src.id, src.username, src.access_level_id, src.first_name, src.last_name, src.email, src.work_phone )',
'when matched then',
'    update set access_level_id = greatest( dest.access_level_id, src.access_level_id ),',
'        first_name = coalesce( dest.first_name, src.first_name ),',
'        last_name  = coalesce( dest.last_name, src.last_name ),',
'        email      = coalesce( dest.email, src.email ),',
'        work_phone = coalesce( dest.work_phone, src.work_phone );',
'',
'alter table  eba_bt_user_group drop constraint eba_bt_user_group_user_fk;',
'/',
'alter table  eba_bt_sw_defects drop constraint eba_bt_sw_defects_assign_fk;',
'/',
'alter table  eba_bt_sw_defects drop constraint eba_bt_sw_defects_user_fk;',
'/',
'alter table  eba_bt_user_group add constraint eba_bt_user_group_users_fk foreign key (user_id)',
'      references  eba_bt_users(id) on delete cascade enable;',
'/',
'',
'alter table  eba_bt_sw_defects add constraint eba_bt_sw_defects_assign_fk foreign key (assigned_to_id)',
'      references  eba_bt_users(id) on delete set null enable',
'/',
'alter table  eba_bt_sw_defects add constraint eba_bt_sw_defects_user_fk foreign key (submitted_by_id)',
'      references  eba_bt_users(id) on delete set null enable',
'/',
'drop table eba_bt_user',
'/',
'',
'alter table eba_bt_access_levels drop constraint eba_bt_acc_lvl_acc_lvl_ck',
'/',
'alter table eba_bt_access_levels',
'add constraint eba_bt_acc_lvl_acc_lvl_ck',
'    check (access_level in (',
'        ''Administrator'',',
'        ''Contributor'',',
'        ''Reader'',',
'        ''Pending Approval'',',
'        ''No Access''))',
'/',
'insert into eba_bt_access_levels (id, access_level) values (0, ''Pending Approval'');',
'/',
'insert into eba_bt_access_levels (id, access_level) values (-99, ''No Access'');',
'/',
'update eba_bt_users',
'set access_level_id = -99',
'where id < 100;',
'/'))
);
end;
