begin
wwv_flow_api.create_install_script(
 p_id=>wwv_flow_api.id(4399391896409742902)
,p_install_id=>wwv_flow_api.id(8637888567999432334)
,p_name=>'create eba_bt_sw_defects triggers'
,p_sequence=>170
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace trigger biu_eba_bt_sw_defects',
'before insert or update on eba_bt_sw_defects',
'for each row',
'begin',
'    if :new.tags is not null then',
'        :new.tags := eba_bt_fw.tags_cleaner(:new.tags);',
'    end if;',
'   if inserting then',
'      if :NEW.ID is null then',
'         select eba_bt_seq.NEXTVAL',
'           into :NEW.ID',
'           from dual;',
'      end if;',
'      if :NEW.CREATED is null then',
'          :NEW.CREATED    := LOCALTIMESTAMP;',
'      end if;',
'      if :NEW.CREATED_BY is null then',
'          :NEW.CREATED_BY := nvl(v(''APP_USER''),USER);',
'      end if;',
'      :new.row_version_number := 1;',
'   end if;',
'',
'   if updating then',
'      :NEW.UPDATED    := LOCALTIMESTAMP;',
'      :NEW.UPDATED_BY := nvl(v(''APP_USER''),USER);',
'      :new.row_version_number := nvl(:old.row_version_number,1) + 1;',
'   end if;',
'   if :new.bug_number is null then',
'       for c1 in (select eba_bt_seq.nextval s from dual) loop',
'           :new.bug_number := to_char(c1.s);',
'       end loop;',
'   end if;',
'',
'   if :new.severity_id is null then',
'      for c1 in (select id from eba_bt_severity order by sequence_number desc) loop',
'          :new.severity_id := c1.id;',
'          exit;',
'      end loop;',
'   end if;',
'    eba_bt_fw.tag_sync(',
'        p_new_tags      => :new.tags,',
'        p_old_tags      => :old.tags,',
'        p_content_type  => ''DEFECT'',',
'        p_content_id    => :new.id );',
'end;',
'/',
'',
'create or replace trigger bd_eba_bt_sw_defects',
'    before delete on eba_bt_sw_defects',
'    for each row',
'begin',
'',
'    eba_bt_fw.tag_sync(',
'        p_new_tags      => null,',
'        p_old_tags      => :old.tags,',
'        p_content_type  => ''DEFECT'',',
'        p_content_id    => :old.id );',
'',
'end;',
'/',
''))
);
end;
