'/',
'    ',
'create or replace package body eba_bt_import as',
'function de_quote( p_str in varchar2, p_enc_by in varchar2 ) return varchar2',
'is      ',
'    l_str        varchar2(32767) := null;',
'    s            number := null;   ',
'    e            number := null;',
'begin      ',
'      if ( p_enc_by is null )',
'      then',
'          return p_str;',
'      else       ',
'          l_str := p_str;',
'          s := instr(l_str,p_enc_by,1);',
'          -- if string is enlosed, remove enclosed char',
'          if s = 1 then',
'              s := s + length(p_enc_by);              ',
'              l_str := substr(l_str,s,length(l_str));',
'              e := instr(l_str,p_enc_by,-1);',
'              e := e - length(p_enc_by);',
'              l_str := substr(l_str,1,e);      ',
'          end if;    ',
'          ',
'          -- if string has escaped char, unescape. i.e. 54"" Plasma becomes 54" Plasma      ',
'          l_str := replace( l_str, p_enc_by||p_enc_by, p_enc_by );',
'        ',
'          return l_str;',
'      end if;',
'exception when others then',
'    raise_application_error(-20001,''de_quote : ''||sqlerrm);',
'end de_quote;',
'-- -----------------------------------------------------------------------------------',
'-- get query columns',
'--',
'function get_line(',
'    p_data          in clob,   ',
'    p_line          out varchar2,    ',
'    p_line_num      in out pls_integer,',
'    p_nxtlineoffset in out pls_integer) return boolean',
'is',
'    l_n             pls_integer;',
'    l_ateof         boolean := FALSE;',
'    ',
'    l_amt           number;',
'    l_errmsg        varchar2(32767) := null;',
'    l_errline       varchar2(4000) := null;  ',
'begin',
'    l_n := sys.dbms_lob.instr(lob_loc => p_data, pattern => unistr(''\000a''), offset => p_nxtlineoffset);',
'    ',
'    if (l_n = 0) then                                   -- End of file or no unistr(''\000a'') (aka newline) prior to EOF',
'        l_n := nvl(sys.dbms_lob.getlength(p_data),0);   -- use end of string as end of line',
'        if (l_n < p_nxtlineoffset) then                 -- if intended to look beyond EOF',
'            l_n := 0;                                   -- really was EOF',
'        else',
'            l_n := l_n + 1;                             -- make the substr below give complete end of string',
'        end if;',
'    end if;',
'    ',
'    if (l_n = 0) then -- End of file',
'        p_line := NULL;     ',
'        p_line_num := 0;      ',
'        p_nxtlineoffset := 0;',
'        l_ateof := TRUE;',
'    elsif (l_n - p_nxtlineoffset > 32767) then',
'        -- If line length > 32767 characters and p_log is true,',
'        -- log unable to parse 32KB row.',
'        p_line_num := p_line_num + 1;      ',
'        p_nxtlineoffset := l_n + 1;',
'        p_line := null;        ',
'    else',
'        l_amt := l_n - p_nxtlineoffset;',
'        ',
'        -- Since there is no lengh parameter in calling wwv_flow_utilities.clob_to_varchar2,',
'        -- calling the function every time up to 32K and substr out the amount needed will decrease the performance.',
'        -- Since DBMS_LOB.SUBSTR in 10g will return max of 8191 characters in UTF8,',
'        -- if l_amt < 8191 characters, call dbms_lob.substr instead.',
'        -- If not, call wwv_flow_utilities.clob_to_varchar2.',
'        -- If wwv_flow_utilities.clob_to_varchar2 encounters an ORA-06502 PL/SQL numeric or value error, we''re trying',
'        -- to get more than 32767 bytes. Log unable to parse 32KB row.',
'        if l_amt < 8191 then',
'          p_line := sys.dbms_lob.substr(lob_loc => p_data, amount => l_amt, offset => p_nxtlineoffset);',
'        else',
'          begin',
'              p_line := wwv_flow_utilities.clob_to_varchar2(p_clob => p_data, p_offset => p_nxtlineoffset-1, p_raise => true);',
'          exception',
'              when others then',
'                  if sqlcode = -6502 then',
'                      p_line := null;                      ',
'                  else',
'                      raise;',
'                  end if;',
'          end;',
'            p_line := substr(p_line,1,l_amt);',
'        end if;',
'        p_line_num := p_line_num + 1;      ',
'        p_nxtlineoffset := l_n + 1;',
'    end if;',
'    return l_ateof;',
'exception when others then',
'    raise_application_error(-20001,''get_line error ''||sqlerrm);    ',
'end get_line;',
'function get_column(',
'    p_line          in varchar2,',
'    p_delim         in varchar2,',
'    p_column        out varchar2,    ',
'    p_col_elmt      in out pls_integer,',
'    p_nxtcoloffset  in out pls_integer',
'    ) return boolean',
'is',
'    l_d             pls_integer;',
'    l_ateol         boolean := FALSE;',
'begin',
'    l_d := nvl(instr(p_line, p_delim, p_nxtcoloffset),0);    ',
'    if (l_d = 0) then                            -- End of line or no delim. found prior to EOL',
'      l_d := nvl(length(p_line),0);              -- use end of line as end of column',
'      if (l_d < p_nxtcoloffset) then             -- if intended to look beyond EOL',
'        l_d := 0;                                -- really was EOL',
'      else',
'        l_d := l_d + 1;                          -- make the substr below give complete end of string',
'      end if;',
'    end if;',
'    ',
'    if (l_d = 0) then -- End of file',
'      p_column := NULL;     ',
'      p_col_elmt := 0;      ',
'      p_nxtcoloffset := 0;',
'      l_ateol := TRUE;    ',
'    else',
'      p_column := substr(p_line,p_nxtcoloffset,l_d - p_nxtcoloffset);             ',
'      p_col_elmt := p_col_elmt + 1;      ',
'      p_nxtcoloffset := l_d + 1;',
'    end if;   ',
'    ',
'    return l_ateol;',
'exception when others then',
'    raise_application_error(-20001,''get_column error ''||sqlerrm);    ',
'end get_column;',
